CREATE DATABASE The_Human_Genetic_Infertility_Database DEFAULT CHARACTER SET 'latin1';

CREATE TABLE The_Human_Genetic_Infertility_Database.Parents (
    Parent_ID VARCHAR (10) NOT NULL,
	Sample_Number INTEGER,
    Sex VARCHAR(1) DEFAULT NULL CHECK (Sex IN ('','M','F')),
    Age VARCHAR(20),
    Ethnicity VARCHAR(20),
    Genome_Case_Type VARCHAR(30) NOT NULL,
    PRIMARY KEY(Parent_ID)
);

CREATE TABLE The_Human_Genetic_Infertility_Database.Couples (
	Couple_ID VARCHAR(10) NOT NULL,
	Mother_ID VARCHAR(10) UNIQUE NOT NULL,
	Father_ID VARCHAR(10) UNIQUE NOT NULL,
	PRIMARY KEY(Couple_ID),
	FOREIGN KEY (Mother_ID) REFERENCES Parents(Parent_ID) ON UPDATE CASCADE,
	FOREIGN KEY (Father_ID) REFERENCES Parents(Parent_ID) ON UPDATE CASCADE
);


CREATE TABLE The_Human_Genetic_Infertility_Database.Patient_Reproductive_History (
	Parent_ID VARCHAR(10) NOT NULL PRIMARY KEY,
	Fertile VARCHAR(1) DEFAULT NULL CHECK (Fertile IN ('', 'F', 'I')),
    Referral_Reason VARCHAR(100) NOT NULL,
    Reproductive_History VARCHAR(250),
	FOREIGN KEY (Parent_ID) REFERENCES Parents(Parent_ID) ON UPDATE CASCADE
);


CREATE TABLE The_Human_Genetic_Infertility_Database.Fertility_Treatment (
	Couple_ID VARCHAR(10) NOT NULL,
	Fertility_Treatment_Type VARCHAR(20) NOT NULL,
	Number_of_Cycles VARCHAR(150) NOT NULL,
	Successful_Pregnancy VARCHAR(100) NOT NULL,
    PRIMARY KEY (Couple_ID),
    FOREIGN KEY (Couple_ID) REFERENCES Couples(Couple_ID) ON UPDATE CASCADE
);

CREATE TABLE The_Human_Genetic_Infertility_Database.Fertility_Treatment_Summary (
	Couple_ID VARCHAR(10) NOT NULL,
    Successful_Cycles INTEGER NOT NULL,
	PRIMARY KEY (Couple_ID),
    FOREIGN KEY (Couple_ID) REFERENCES Fertility_Treatment(Couple_ID) ON UPDATE CASCADE
);
    
CREATE TABLE The_Human_Genetic_Infertility_Database.Sperm_Parameters (
	Couple_ID VARCHAR(10) NOT NULL,
    Father_ID VARCHAR(10) UNIQUE NOT NULL,
    Sperm_Parameter VARCHAR(100),
    Sperm_Details VARCHAR(100),
    PRIMARY KEY (Couple_ID),
    FOREIGN KEY (Couple_ID) REFERENCES Couples(Couple_ID) ON UPDATE CASCADE,
    FOREIGN KEY (Father_ID) REFERENCES Couples(Father_ID) ON UPDATE CASCADE
);


CREATE TABLE The_Human_Genetic_Infertility_Database.PGD_Information (
	Couple_ID VARCHAR(10) NOT NULL,
    PGD_ID INTEGER NOT NULL,
	Number_of_Embryos_used INTEGER NOT NULL,
    PRIMARY KEY (Couple_ID, PGD_ID),
    FOREIGN KEY (Couple_ID, PGD_ID) REFERENCES Couples(Couple_ID, PGD_ID) ON UPDATE CASCADE
);

CREATE TABLE The_Human_Genetic_Infertility_Database.PGD_Results_Type (
   Embryo_Result_Type VARCHAR(70) NOT NULL PRIMARY KEY,
   Result_Type_Description TEXT
);

CREATE TABLE The_Human_Genetic_Infertility_Database.PGD_Results (
	Couple_ID VARCHAR(10) NOT NULL,
	Embryo_Result_Type VARCHAR(70) NOT NULL,
	Number_of_Embryos_Affected INTEGER,
	Result_Type_Description VARCHAR(500),
	PRIMARY KEY (Couple_ID, Embryo_Result_Type),
	FOREIGN KEY (Couple_ID) REFERENCES Couples(Couple_ID) ON UPDATE CASCADE,
	FOREIGN KEY (Embryo_Result_Type) REFERENCES PGD_Results_Type(Embryo_Result_Type) ON UPDATE CASCADE
);

CREATE TABLE The_Human_Genetic_Infertility_Database.Patient_Genomics (
	Parent_ID VARCHAR(10) NOT NULL,
	Amplification_Length_bp INTEGER,
	Deletion_Length_bp INTEGER,
	Variants_Affects_downstream INTEGER,
	Variants_Affects_upstream INTEGER,
	Variants_Affects_exon_region INTEGER,
	Variants_Affects_intron_region INTEGER,
	Variants_Affects_intergenic_region INTEGER,
	Variants_Affects_splicing INTEGER,
	Variants_Affects_NcRNA INTEGER,
	Variants_Affects_3_UTR INTEGER,
	Variants_Affects_5_UTR INTEGER,
	Total_CNVs INTEGER,
	PRIMARY KEY (Parent_ID),
	FOREIGN KEY (Parent_ID) REFERENCES Parents(Parent_ID) ON UPDATE CASCADE	
);

